import React from 'react';
import './App.css';
import MainPage from './mainPage/mainpage';
import SignInPage from './signInPage/signinpage';
import RegisterPage from './registerPage/registerpage';
import Nav from './nav/nav';
import brain from './icons8-brain-96.png';



class App extends React.Component {


  constructor() {
    super()
    this.state={
      route: 'main'
    }
  }

  updateImage = (_evnt) => {
    this.setState({imageUrl: _evnt.target.value});
  }

  onRouteChange = (route) => {
    this.setState({route: route});
  }

  render() {

    let route = (<RegisterPage />);

    if (this.state.route === 'main') { route = (<MainPage />)} else if (this.state.route === 'signin') {route = (<SignInPage />)} else {route = (<RegisterPage onRouteChange={this.onRouteChange} />)};

    return (
      <div className="App">
        <Nav onRouteChange={this.onRouteChange} />
        <div className='logo'>
          <span className='glassmorph'><img alt='brain logo' src={brain} width='100px' height='auto'/></span>
        </div>
        { route }
      </div>
    );
  }
}

export default App;
